import React from 'react'
import {Fragment, useState, useEffect } from 'react'
// const cors = require('cors');
// app.use(cors());
export const Card = () => {
    const [infos, setinfos] = useState([]);
    const getinfo = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/v1/employee');
            const jsonData = await response.json();

            setinfos(jsonData);
            console.log("hi",jsonData)
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getinfo();
    }, []);

    console.log(infos);


    // const [employees, setEmployees] = useState([]);

    // useEffect(() => {
    //     fetch('http://localhost:3000/api/v1/employee')
    //         .then(response => response.json()) // Convert the response to JSON
    //         .then(data => setEmployees(data)) // Store the data in the state
    //         .catch(error => console.error('Error fetching data:', error));
    // }, []);

    // console.log("hi",employees)

  return (
    <Fragment>
    {" "}
    {infos.map(info => (
    <div class="flex flex-wrap justify-center mt-10">
      {info}
    <div class="p-4 max-w-sm">
        <div class="flex rounded-lg h-full dark:bg-gray-800 bg-teal-400 p-8 flex-col">
            <div class="flex items-center mb-3">
                <div
                    class="w-8 h-8 mr-3 inline-flex items-center justify-center rounded-full dark:bg-indigo-500 bg-indigo-500 text-white flex-shrink-0">
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-5 h-5" viewBox="0 0 24 24">
                        <path d="M22 12h-4l-3 9L9 3l-3 9H2"></path>
                    </svg>
                </div>
                <h2 class="text-white dark:text-white text-lg font-medium">{}</h2>
            </div>
            <div class="flex flex-col justify-between flex-grow">
                <p class="leading-relaxed text-base text-white dark:text-gray-300">
                    Blue bottle crucifix vinyl post-ironic four dollar toast vegan taxidermy. Gastropub indxgo juice poutine.
                </p>
                <a href="#" class="mt-3 text-black dark:text-white hover:text-blue-600 inline-flex items-center">Learn More
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-4 h-4 ml-2" viewBox="0 0 24 24">
                        <path d="M5 12h14M12 5l7 7-7 7"></path>
                    </svg>
                </a>
            </div>
        </div>
        </div>
        </div>
      ))}

</Fragment>
  )
}
