// import React from 'react'
import React, { Fragment, useState } from "react";
export const CardUpdate = ({info}) => {
// insert Value
  const [e_name,setename] = useState(info.e_name);
  const [e_email,sete_email] = useState(info.e_email);
  const [e_age,sete_age]=useState(info.e_age);
  const [e_salary,sete_salary]=useState(info.e_salary);
  const [e_address,sete_address] = useState(info.e_address);
  const [e_joining_date,sete_joining_date]=useState(info.e_joining_date);
  const [e_total_sell,sete_total_sell] = useState(info.e_total_sell);
  const [showModal, setShowModal] = React.useState(false);

  const updateinfo = async e =>{
    e.prevenDefault();
    try {
        const body = { e_name,e_email,e_age,e_salary,e_address,e_joining_date,e_total_sell  };
        const response = await fetch(`http://localhost:3000/api/v1/employee/${info.id}`, 
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });
        console.log(response)
        window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
        };


  return (
<Fragment>
<button
  type="button"
  className="btn btn-warning"
  onClick={() => setShowModal(true)}
>
  Edit
</button>

{showModal && (
  <div className="modal" id={`id${info.id}`}>
    <div className="modal-dialog">
      <div className="modal-content">
        <div className="modal-header">
          <h4 className="modal-title">Edit info</h4>
          <button
            type="button"
            className="close"
            onClick={() => setShowModal(false)}
          >
            &times;
          </button>
        </div>

        <div className="modal-body">
          <input
            type="text"
            className="form-control"
            value={e_name}
            onChange={(e) => setename(e.target.value)}
          />
        </div>

        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-warning"
            onClick={(e) => updateinfo(e)}
          >
            Edit
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={() => setShowModal(false)}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  </div>
)}

</Fragment>
  );
};

export default CardUpdate
