import Joi from '@hapi/joi';

// Define a Joi schema
const schema = Joi.object({
    e_name: Joi.string().min(3).max(30).required().label('Name'),
    e_email: Joi.string().email({ tlds: { allow: false } }).required().label('Email'),
    e_age: Joi.number().integer().min(18).max(65).required().label('Age'),
    e_salary: Joi.number().positive().required().label('Salary'),
    e_address: Joi.string().min(5).required().label('Address'),
    e_joining_date: Joi.date().iso().required().label('Hiring Date'),
    e_total_sell: Joi.number().positive().required().label('Total Sale')
});
