// import React from 'react'
import React, { Fragment, useState } from "react";
export const CardUpdate = ({info}) => {

// insert Value
  const [e_name,setename] = useState(info.e_name);
  const [e_email,sete_email] = useState(info.e_email);
  const [e_age,sete_age]=useState(info.e_age);
  const [e_salary,sete_salary]=useState(info.e_salary);
  const [e_address,sete_address] = useState(info.e_address);
  const [e_joining_date,sete_joining_date]=useState(info.e_joining_date);
  const [e_total_sell,sete_total_sell] = useState(info.e_total_sell);


  const updateinfo =async e =>{
    e.prevenDefault();
    try {
        const body = { e_name,e_email,e_age,e_salary,e_address,e_joining_date,e_total_sell  };
        const response = await fetch(`http://localhost:3000/api/v1/employee/${info.id}`, 
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });
        window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
        };

const [showModal, setShowModal] = React.useState(false);
  return (
<Fragment>

      <button
        className="bg-pink-500 text-white active:bg-pink-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
        type="button"
        onClick={() => setShowModal(true)}
      >
        Edit
      </button>
      {showModal ? (
        <>
          <div
            className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
          >
            <div className="relative w-auto my-6 mx-auto max-w-3xl">

              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                {/*header*/}
                <div className="flex items-start justify-between p-5 border-b border-solid border-blueGray-200 rounded-t">
                  <h3 className="text-3xl font-semibold">
                    Modal Title
                  </h3>
                  <button
                    className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                    onClick={() => setShowModal(false)}
                  >
                    <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                      ×
                    </span>
                  </button>
                </div>
                {/*body*/}
                <div className="relative p-6 flex-auto">
                  <p className="my-4 text-blueGray-500 text-lg leading-relaxed">
                    I always felt like I could do anything. That’s the main
                    thing people are controlled by! Thoughts- their perception
                    of themselves! They're slowed down by their perception of
                    themselves. If you're taught you can’t do anything, you
                    won’t do anything. I was taught I could do everything.
                  </p>
                </div>
                {/*footer*/}
                <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                  <button
                    className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={() => setShowModal(false)}
                  >
                    Close
                  </button>
                  <button
                    className="bg-emerald-500 text-white active:bg-emerald-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={() => setShowModal(false)}
                  >
                    Save Changes
                  </button>
                </div>
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}


  <button
    type="button"
    class="btn btn-warning"
    data-toggle="modal"
    data-target={`#id${info.info_id}`}
  >
    Edit
  </button>

  {/* 
    id = id10
  */}
  <div
    class="modal"
    id={`id${info.id}`}
    onClick={() => setename(info.e_name)}
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h4 class="modal-title">Edit info</h4>
          <button
            type="button"
            class="close"
            data-dismiss="modal"
            onClick={() => setename(info.e_name)}
          >
            &times;
          </button>
        </div>

        <div class="modal-body">
          <input
            type="text"
            className="form-control"
            value={e_name}
            onChange={e => setename(e.target.value)}
          />
        </div>

        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-warning"
            data-dismiss="modal"
            onClick={e => updateinfo(e)}
          >
            Edit
          </button>
          <button
            type="button"
            class="btn btn-danger"
            data-dismiss="modal"
            onClick={() => sete_age(info.e_age)}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  </div>
</Fragment>
  );
};

export default CardUpdate
