import React, { Fragment, useState } from "react";
export const Createcard = () => {

// insert Value
  const [e_name,setename] = useState("");
  const [e_email,sete_email] = useState("");
  const [e_age,sete_age]=useState("");
  const [e_salary,sete_salary]=useState("");
  const [e_address,sete_address] = useState("");
  const [e_joining_date,sete_joining_date]=useState("");
  const [e_total_sell,sete_total_sell] = useState("");

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { e_name,e_email,e_age,e_salary,e_address,e_joining_date,e_total_sell };
      const response = await fetch("http://localhost:3000/api/v1/employee", {
        method: "post",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };

// delete


  return (
    <Fragment>
      <div class="container-sm">
        <div>
          <form method="POST" className="reg-form" onSubmit={onSubmitForm}>
            <div class="sm-3">
              <label class="form-label">First Name</label>
              <input
                type="text"
                class="form-control"
                value={firstname}
                onChange={(e) => setename(e.target.value)}
                id="firstname"/>
            </div>
            <div class="sm-3">
              <label class="form-label">Last Name</label>
              <input
                type="text"
                class="form-control"
                value={lastname}
                onChange={(e) => sete_email(e.target.value)}
                id="lastname"/>
            </div>
            <div class="sm-3">
              <label class="form-label">Email address</label>
              <input
                type="email"
                class="form-control"
                value={email}
                onChange={(e) => sete_age(e.target.value)} />
            </div>
            <div class="sm-3">
              <label class="form-label">Address</label>
              <input
                type="text"
                class="form-control"
                value={address}
                onChange={(e) => sete_address(e.target.value)}
                id="address"/>
            </div>
            <div class="sm-3">
              <label class="form-label">Phone</label>
              <input
                type="number"
                class="form-control"
                value={phone}
                onChange={(e) => sete_joining_date(e.target.value)}
                id="phone"/>
            </div>

            <label class="form-label">Phone</label>
              <input
                type="number"
                class="form-control"
                value={phone}
                onChange={(e) => sete_total_sell(e.target.value)}
                id="phone"/>

            <div class="f-grid gap-2 d-md-flex justify-content-md-end">
              <button class="btn btn-primary" type="button" onClick={onSubmitForm}>
                Button
              </button>
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};
