import React from 'react'
import {Fragment, useState, useEffect } from 'react'
import CardUpdate from './CardUpdate'
// const cors = require('cors');
// app.use(cors());
export const Card = () => {
    const [infos, setinfos] = useState([]);

    const deleteinfo = async id => {
      try {
          const response = await fetch(`http://localhost:3000/api/v1/employee/${id}`, 
          {
              method: "DELETE",
              headers: {
                "Content-Type": "application/json"
              }
          });

          setinfos(infos.filter(info => id !== id));
          window.location = "/";
      } catch (err) {
          console.error(err.message);
      }
  };
    const getinfo = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/v1/employee');
            const jsonData = await response.json();

            setinfos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getinfo();
    }, []);

  return (
    <Fragment>
    {" "}
    <div class="text-2xl py-4 px-6 bg-gray-300 text-white text-center font-bold uppercase">
        Client Details
    </div>
    <div class="flex flex-wrap justify-center mt-10">

    {infos.map(info => (


    <div class="p-4 max-w-sm">
        <div class="flex rounded-lg h-full  background-colo bg-blue-500 p-8 flex-col">
            <div class="flex items-center mb-3">
                <div
                    class="w-8 h-8 mr-3 inline-flex items-center justify-center rounded-full bg-indigo-500 text-white flex-shrink-0">
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-5 h-5" viewBox="0 0 24 24">
                        <path d="M22 12h-4l-3 9L9 3l-3 9H2"></path>
                    </svg>
                </div>
                <h2 class="text-white dark:text-white text-lg font-medium">{info.e_name}</h2>
            </div>
            <div class="flex flex-col justify-between flex-grow">
                  <ul class="leading-relaxed text-base text-white dark:text-gray-300">
              <li>Email :{info.e_email},</li> 
              <li> Age: {info.e_age},</li> 
              <li>  Salary:{info.e_salary},</li> 
               <li>  Address: {info.e_address},</li> 
               <li> Hiring Date: {info.e_joining_date},</li> 
               <li> Total Sell: {info.e_total_sell}</li> 
               <li><CardUpdate info={info} /></li>
                </ul>

                <button
                    className="bg-indigo-500 rounded-tl-full rounded-br-full text-white text-sm text-center self-center px-4 py-2 m-2"
                    id="YOUR_ID" 
                    onClick={() => deleteinfo(info.id)}
                >
                  Delete
                  </button>




















                {/* <a href="#" class="mt-3 text-black dark:text-white hover:text-blue-600 inline-flex items-center"> 
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-4 h-4 ml-2" viewBox="0 0 24 24">
                        <path d="M5 12h14M12 5l7 7-7 7"></path>
                    </svg>
                </a> */}
            </div>
        </div>
        </div>

      ))}
    </div>


    <div 
    class="fixed z-50 inset-0 overflow-y-auto">
    <div class="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">

        <div class="fixed inset-0 transition-opacity" aria-hidden="true">
            <div class="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>

        <span class="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>


        <div class="inline-block align-bottom bg-white rounded-lg px-4 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6"
            role="dialog" aria-modal="true" aria-labelledby="modal-headline">
            <div class="hidden sm:block absolute top-0 right-0 pt-4 pr-4">
                <button type="button" data-behavior="cancel" class="bg-white rounded-md text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                    <span class="sr-only">Close</span>
                    <svg class="h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                </button>
            </div>
            <div class="sm:flex sm:items-start">
                <div
                    class="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-blue-100 sm:mx-0 sm:h-10 sm:w-10">
                    <svg class="h-6 w-6 text-blue-600" xmlns="http://www.w3.org/2000/svg" fill="none"
                        viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2"
                            d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                    </svg>
                </div>
                <div class="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3 class="text-lg leading-6 font-medium text-gray-900" id="modal-headline">
                        Your Confirmation Message
                    </h3>
                    <div class="mt-2">
                        <p class="text-sm text-gray-500">
                            Your body text goes here.
                        </p>
                    </div>
                </div>
            </div>
            <div class="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
                <button type="button" data-behavior="commit" class="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:ml-3 sm:w-auto sm:text-sm">
                    Commit
                </button>
                <button type="button" data-behavior="cancel" class="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:mt-0 sm:w-auto sm:text-sm">
                    Cancel
                </button>
            </div>
        </div>
    </div>
</div>
</Fragment>
  )
}
