import React, { Fragment, useState } from "react";
import Joi from "joi";

export const Createcard = () => {
  const [e_name, setename] = useState("");
  const [e_email, sete_email] = useState("");
  const [e_age, sete_age] = useState("");
  const [e_salary, sete_salary] = useState("");
  const [e_address, sete_address] = useState("");
  const [e_joining_date, sete_joining_date] = useState("");
  const [e_total_sell, sete_total_sell] = useState("");
  const [errors, setErrors] = useState({});
  // Define the Joi schema for validation
  const schema = Joi.object({
    e_name: Joi.string().min(3).required(),
    e_email: Joi.string().email({ tlds: { allow: false } }).required(),
    e_age: Joi.number().integer().min(18).max(65).required(),
    e_salary: Joi.number().min(0).required(),
    e_address: Joi.string().min(5).required(),
    e_joining_date: Joi.date().required(),
    e_total_sell: Joi.number().min(0).required(),
  });

  const onSubmitForm = async (e) => {
    e.preventDefault();
    const body = { e_name, e_email, e_age, e_salary, e_address, e_joining_date, e_total_sell };

    // Validate the form data
    // const { error } = schema.validate(body, { abortEarly: false });

    // if (error) {
    //   console.error("Validation Error:", error.details);
    //   alert("Please correct the highlighted errors before submitting.");
    //   return;
    // }

// Validate the form data
const { error } = schema.validate(body, { abortEarly: false });

if (error) {
  const errorMessages = {};
  error.details.forEach((detail) => {
    errorMessages[detail.path[0]] = detail.message;
  });
  setErrors(errorMessages);
  return;
}

setErrors({});




    try {
      const response = await fetch("http://localhost:3000/api/v1/employee", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      console.log("Success:", data);
      window.location = "/";
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <Fragment>
      <div className="max-w-md mx-auto mt-10 bg-white shadow-lg rounded-lg overflow-hidden">
        <div className="text-2xl py-4 px-6 bg-gray-900 text-white text-center font-bold uppercase">
          Book an Appointment
        </div>
        <form className="py-4 px-6" onSubmit={onSubmitForm}>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="name">
              Name
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_name}
              onChange={(e) => setename(e.target.value)}
              id="e_name"
              required
            />
             {errors.e_name && <p className="text-red-500 text-xs mt-1">{errors.e_name}</p>}
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
              Email
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              type="email"
              value={e_email}
              onChange={(e) => sete_email(e.target.value)}
              id="e_email"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="age">
              Age
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_age}
              onChange={(e) => sete_age(e.target.value)}
              id="e_age"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="salary">
              Salary
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_salary}
              onChange={(e) => sete_salary(e.target.value)}
              id="e_salary"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="address">
              Address
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_address}
              onChange={(e) => sete_address(e.target.value)}
              id="e_address"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="joining_date">
              Hiring Date
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_joining_date}
              onChange={(e) => sete_joining_date(e.target.value)}
              id="e_joining_date"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2" htmlFor="total_sell">
              Total Sale
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={e_total_sell}
              onChange={(e) => sete_total_sell(e.target.value)}
              id="e_total_sell"
              required
            />
          </div>
          <div className="flex items-center justify-center mb-4">
            <button
              className="bg-gray-900 text-white py-2 px-4 rounded hover:bg-gray-800 focus:outline-none focus:shadow-outline"
              type="submit"
            >
              Book Appointment
            </button>
          </div>
        </form>
      </div>
    </Fragment>
  );
};
