import React, { useState } from "react";

const CardUpdate = ({ info }) => {
  const [e_name, setename] = useState(info.e_name);
  const [showModal, setShowModal] = useState(false);

  const updateinfo = async (e) => {
    e.preventDefault();
    try {
      const body = { e_name };
      const response = await fetch(`http://localhost:3000/api/v1/employee/${info.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      console.log(response);
      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
        <button
        type="button"
        className="bg-yellow-500 text-white font-bold py-2 px-4 rounded"
        data-target={`#id${info.info_id}`}
        id={`id${info.id}`}
        onClick={() => {
        setename(info.e_name);
        setShowModal(true);
        }}
      >
        Edit
      </button>

      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
          <div className="relative w-auto max-w-3xl mx-auto my-6">
            <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
              <div className="flex items-start justify-between p-5 border-b border-solid border-gray-300 rounded-t">
                <h3 className="text-3xl  text-black font-semibold">Edit Info</h3>

                <button
                  className="p-1 ml-auto bg-transparent border-0 text-black float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                  onClick={() => setShowModal(false)}
                >
                  <span className="bg-transparent text-black h-6 w-6 text-2xl block outline-none focus:outline-none">
                    Ã—
                  </span>
                </button>
              </div>

              <div className="relative p-6 flex-auto">
                <input
                  type="text"
                  className="w-full p-2 border border-gray-300 rounded mt-2"
                  value={e_name}
                  onChange={(e) => setename(e.target.value)}
                />
              </div>



              <div class="max-w-md mx-auto mt-10 bg-white shadow-lg rounded-lg overflow-hidden">
    <div class="text-2xl py-4 px-6 bg-gray-900 text-white text-center font-bold uppercase">
        Book an Appointment
    </div>
    <form class="py-4 px-6" action="" method="POST">
        <div class="mb-4">
            <label class="block text-gray-700 font-bold mb-2" for="name">
                Name
            </label>
            <input
                class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={e_name}
                onChange={(e) => setename(e.target.value)}
                id ="e_name"/>
        </div>
        <div class="mb-4">
            <label class="block text-gray-700 font-bold mb-2" for="email">
                Email
            </label>
            <input
                class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"

                value={e_email}
                onChange={(e) => sete_email(e.target.value)}
                id ="e_email"/>

        </div>
        <div class="mb-4">
            <label class="block text-gray-700 font-bold mb-2" for="phone">
                Phone Number
            </label>
            <input
                class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={e_age}
                onChange={(e) => sete_age(e.target.value)}
                id ="e_age"/>
        </div>
        <div class="mb-4">
            <label class="block text-gray-700 font-bold mb-2" for="date">
                Date
