import React from 'react'
import {Fragment, useState, useEffect } from 'react'
import CardUpdate from './CardUpdate'
// const cors = require('cors');
// app.use(cors());
export const Card = () => {
    const [infos, setinfos] = useState([]);

    const deleteinfo = async id => {
      try {
          const response = await fetch(`http://localhost:3000/api/v1/employee/${id}`, 
          {
              method: "DELETE",
              headers: {
                "Content-Type": "application/json"
            }
          });

          if (!response.ok) {
            throw new Error('Failed to delete employee');
        }

        const result = await response.json();
        // console.log(result);
        window.location.reload();

    } catch (err) {
        console.error(err.message);
    }
  };
    const getinfo = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/v1/employee');
            const jsonData = await response.json();

            setinfos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getinfo();
    }, []);

  return (
    <Fragment>
    {" "}
    <div class="text-2xl py-4 px-6 bg-gray-300 text-white text-center font-bold uppercase">
        Client Details
    </div>
    <div class="flex flex-wrap justify-center mt-10">

    {infos.map(info => (


    <div class="p-4 max-w-sm">
        <div class="flex rounded-lg h-full  background-colo bg-blue-500 p-8 flex-col">
            <div class="flex items-center mb-3">
                <div
                    class="w-8 h-8 mr-3 inline-flex items-center justify-center rounded-full bg-indigo-500 text-white flex-shrink-0">
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-5 h-5" viewBox="0 0 24 24">
                        <path d="M22 12h-4l-3 9L9 3l-3 9H2"></path>
                    </svg>
                </div>
                <h2 class="text-white dark:text-white text-lg font-medium">{info.e_name}</h2>
            </div>
            <div class="flex flex-col justify-between flex-grow">
                  <ul class="leading-relaxed text-base text-white dark:text-gray-300">
              <li>Email :{info.e_email},</li> 
              <li> Age: {info.e_age},</li> 
              <li>  Salary:{info.e_salary},</li> 
               <li>  Address: {info.e_address},</li> 
               <li> Hiring Date: {info.e_joining_date},</li> 
               <li> Total Sell: {info.e_total_sell}</li> 
               <li><CardUpdate info={info} /></li>
               {/* <li><CardUpdate info={info} /></li */}
                </ul>
                <button
                    className="btn btn-danger"
                    onClick={() => deleteinfo(info.id)}
                >
                  Delete
                  </button>
                <a href="#" class="mt-3 text-black dark:text-white hover:text-blue-600 inline-flex items-center"> 
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round"
                        stroke-width="2" class="w-4 h-4 ml-2" viewBox="0 0 24 24">
                        <path d="M5 12h14M12 5l7 7-7 7"></path>
                    </svg>
                </a>
            </div>
        </div>
        </div>

      ))}
    </div>
</Fragment>
  )
}
